const message = $input.first().json.output;

//{"dispositivo":"<device>","bomba":<true/false>}

let ligarBomba = false;

if (message.command === "TURN_ON") {
  ligarBomba = true;
} else if (message.command === "TURN_OFF") {
  ligarBomba = false;
}

return [{
  dispositivo: "human",
  bomba: ligarBomba
}];

const data = $input.first().json;

// Timestamp internacional + timezone correto
const now = new Date().toLocaleString("sv-SE", {
  timeZone: "America/Sao_Paulo",
  hour12: false
});

// Converte hour para n√∫mero
const hour = Number(
  new Date().toLocaleString("en-US", { 
    timeZone: "America/Sao_Paulo", 
    hour: "numeric", 
    hour12: false 
  })
);

// Per√≠odo
let periodo = "madrugada";
if (hour >= 6 && hour < 12) periodo = "manh√£";
else if (hour >= 12 && hour < 18) periodo = "tarde";
else if (hour >= 18) periodo = "noite";

return [{
  key: "irrigacao",
  value: {
    dispositivo: data.dispositivo,
    StatusBomba: data.bomba,
    origemDoComando: "human",
    timestamp: now,
    periodo
  }
}];
